mapper.select:
select ads.id,ads.pck,ads.version,ads.state,ads.confirm,adc.alt,ads.ctime,ads.name,ads.channel
from cms_ad_state ads
left join cms_ad_channel adc on ads.channel = adc.name
left join cms_app ma on ma.exp = ads.name
left join cms_sys_user su on ma.uid = su.user_id
where ads.id is not null

    and ads.pck like concat('%',#{query.pck},'%')


    and ads.channel = #{query.channel}


    and ads.id = #{query.id}


    and ads.ctime = #{query.NowTime}


    and ads.channel = #{query.name}


    and ma.name like concat('%',#{query.mediaName},'%')


    and ma.name = #{query.mediaName}


    and ads.name = #{query.appName}


    and su.user_id = #{query.userName}

mapper.update:
update cms_ad_state ads left join cms_ad_channel adc on ads.channel = adc.name
ads.version = #{updateAdvertisingForm.version}, ads.channel = #{updateAdvertisingForm.channel}, ads.ctime = #{updateAdvertisingForm.ctime},
ads.id = #{updateAdvertisingForm.id}

update cms_ad_state set state = (case state when 0 then 1 when 1 then 0 else 1 end )
where id in

    #{id}

update cms_ad_state set confirm = (case confirm when 0 then 1 when 1 then 0 else 1 end )
where id in

    #{id}
